def setup_display_emails(apps, schema_editor):
    Article = apps.get_model("submission", "Article")

    for article in Article.objects.all():
        if article.correspondence_author:
            article.frozenauthor_set.filter(
                author__pk=article.correspondence_author.pk,
            ).update(
                display_email=True,
            )
        else:
            first_author = article.frozenauthor_set.all().first()
            if first_author:
                first_author.display_email = True
                first_author.save()


